import React, {Component} from 'react';
import PayCard from '../components/PayCard';
import firebase from "firebase";

class Home extends Component{

getPaymentRequest(){
  var paymentRequestID = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
  console.log("THE MY VAR IS: "+ currentUUID);

  var db = firebase.firestore();
  db.collection("paymentRequests").where("PaymentRequestID", "==", paymentRequestID)
    .get()
    .then(function(querySnapshot) {
        querySnapshot.forEach(function(doc) {
            // doc.data() is never undefined for query doc snapshots
            requestData = doc.data();
            amount = requestData.PaymentAmount;
            phoneNumber = requestData.PhoneNumber; 
            description = requestData.RequestDescription;
            console.log("amount: ", amount, "phone number: ", phoneNumber);
            document.getElementById("description").textContent = "@woodstockspizza requests "+ amount+ " for "+ description; 
        });
    })
    .catch(function(error) {
        console.log("Error getting documents: ", error);
    });
}

  render(){
    var paymentRequestID = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
  console.log("THE MY VAR IS: "+ currentUUID);
    getPaymentRequest(currentUUID);
    return (
      <div>
        <PayCard style={{topMargin:"10px",display:"inline-block"}}/> 
      </div>  
    );
  }
}

export default Home;

